mapper.select:
select e.*, d.name deptName from emp as e left join dept as d on e.dept_id = d.id
e.name like concat('%', #{condition.name}, '%')
and e.gender = #{condition.gender}
and e.entry_date between #{condition.begin} and #{condition.end}
order by update_time desc

select e.*
from emp e
where id = #{id}

select id, begin, end, company, job
from emp_expr
where emp_id = #{id}

mapper.delete:
delete
from emp where id in

    #{id}

delete
from emp_expr where emp_expr.emp_id in

    #{id}

mapper.update:
update emp
set username   = #{emp.username}
  , password   = #{emp.password}
  , name=#{emp.name}
  , gender=#{emp.gender}
  , phone=#{emp.phone}
  , job=#{emp.job}
  , salary=#{emp.salary}
  , image=#{emp.image}
  , entry_date=#{emp.entryDate}
  , dept_id=#{emp.deptId}
  , update_time=#{emp.updateTime}
where id = #{emp.id}

mapper.select:
select a.id,
       a.name,
       a.username
from emp a


        a.username = #{emp.username}


        and a.password = #{emp.password}
